xrx:service.xrx:body:
	  {
	  (: get user name, id and type from params :)
	  let $user := xmldb:encode(request:get-parameter("user","0"))
	  let $anno-id := xmldb:encode(request:get-parameter("id","0"))
	  let $type := xmldb:encode(request:get-parameter("type","0"))

	  (: variables to create save path :)
	  let $registerid := substring-before(substring-after($data, 'registerid='), '?!')
	  let $folio := substring-before(substring-after($data, 'folio='), '?!')

	  (: create annotation :)
	  let $annotation := 
	  								if($type = 'privat')then
	  									annotation:delete-privat-surface($data, $user, $anno-id)
	  								else
	  									annotation:delete-public-surface($data, $user, $anno-id)

	  (: atom post parameters :)
	  let $annotation-path := 
						  if($type = 'privat')then
						  	xmldb:encode-uri(concat(conf:param('xrx-user-atom-base-uri'), xmldb:decode($user), '/metadata.annotation.private/', xmldb:decode($registerid), '/', xmldb:decode($folio), '/'))
						  else
						  	xmldb:encode-uri(concat(conf:param('annotation-atom-base-uri'), xmldb:decode($registerid), '/', xmldb:decode($folio), '/'))
	  let $file-name := concat(translate($anno-id, '.', '_'), '.xml')

	  (: atom post :)
	  let $save-annotation := atom:POST($annotation-path, $file-name, $annotation)
	  return
        string("delete-surface")
	  }
